import React,{useEffect,useState} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Alert from '@material-ui/lab/Alert';
import { Redirect } from 'react-router-dom';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import {Link} from 'react-router-dom'
import { GET_ALL_MANS,DELETE_MAN_ID } from '../api/apiService';
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    marginTop:20
  },
  paper:{
    width:'100%',
    margin:'auto'
  },
  removeLink:{
    textDecoration:'none'
  }
}));
export default function Home() {
  const classes = useStyles();
  const [mans,setMans] = useState({});
  const [checkDeleteMan,setCheckDeleteMan] = useState(false);
  const [close, setClose] = React.useState(false);
  useEffect(() => {
    /* GET ALL mans */
    GET_ALL_MANS(`men`).then(item=>setMans(item.data))
     
  }, [])
  /* Show body HTML */
  const RawHTML = (body,className) =>
    <div className={className} dangerouslySetInnerHTML={{ __html: body.replace(/\n/g, '<br />')}} />
 
  /* DELETE MAN ID */
  const deleteManID = (id)=>{
     
    DELETE_MAN_ID(`men/${id}`).then(item=>{
      console.log(item)
      if(item.data===1){
        setCheckDeleteMan(true);
        /* UPDATE MANS */
        setMans(mans.filter(key=>key.id!==id))
      }
    })
  }
  
  return (
    <div className={classes.root}>
         <Grid container spacing={3}>
            <Grid item xs={12}>
              <Paper className={classes.paper}>
                {checkDeleteMan && <Alert
                  action={
                  <IconButton
                    aria-label="close"
                    color="inherit"
                    size="small"
                    onClick={() => {
                      setClose(true);
                      setCheckDeleteMan(false)
                    }}
                  >
                    <CloseIcon fontSize="inherit" />
                  </IconButton>
                }
                >Detele successfuly</Alert>}
              <TableContainer component={Paper}>
                  <Table className={classes.table} aria-label="simple table">
                    <TableHead>
                      <TableRow>
                        <TableCell>Title</TableCell>
                        <TableCell align="center">Name</TableCell>
                        //<TableCell align="center">Slug</TableCell>
                        //<TableCell align="center">Category</TableCell>
                        <TableCell align="center">Modify</TableCell>
                        <TableCell align="center">Delete</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {mans.length>0 && mans.map((row) => (
                        <TableRow key={row.id}>
                          <TableCell component="th" scope="row">{row.name}</TableCell>
                          <TableCell align="left">{RawHTML(row.body,"body")}</TableCell>
                          <TableCell align="center">{row.slug}</TableCell>
                          <TableCell align="center">
                            <Link to={`/edit/man/${row.id}`} className={classes.removeLink}>
                               <Button size="small" variant="contained" color="primary">Edit</Button></Link>
                            </TableCell>
                          <TableCell align="center">
                            
                              <Button  size="small" variant="contained" color="secondary" onClick={()=>deleteManID(row.id)}>Remove</Button>
                               
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </Paper>
            </Grid>
        </Grid>
    </div>
  )
}
