import React, { useEffect, useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Alert from '@material-ui/lab/Alert';
import { Redirect } from 'react-router-dom';

/*Import api */
import {  POST_ADD_PRODUCT } from '../api/apiService';
const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        marginTop: 20
    },
    paper: {
        padding: theme.spacing(2),
        margin: 'auto',
        maxWidth: 600,
    },
    title: {
        fontSize: 30,
        textAlign: 'center'
    },
    link: {
        padding: 10,
        display: 'inline-block'
    },
    txtInput: {
        width: '98%',
        margin: '1%'
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
}));


export default function Product() {
    const classes = useStyles();
    /* SET ATTRIBUTE FORM ADD PRODUCT */
    const [checkAdd, setCheckAdd] = useState(false);
    const [name, setTitle] = useState(null)
    const [description, setBody] = useState(null)



    /* EVENT CHANGE TEXTFIELD IN FORM */
    const handleChangeTitle = (event) => {
        setTitle(event.target.value)
    }
    const handleChangeBody = (event) => {
        setBody(event.target.value)
    }


    /* EVENT BUTTON SUBMIT FORM ADD PRODUCT */
    const addProduct = (event) => {
        event.preventDefault();
        if (name !== "" && description !== "") {
            let product = {
                Name: name,
                Description: description,
            }
            POST_ADD_PRODUCT(`products`, product).then(item => {
                if (item.data === 1) {
                    setCheckAdd(true);
                    
                }
            })
            alert("Thêm sản phẩm thành công!");
        }
        else {
            alert("Bạn chưa nhập đủ thông tin!");
        }
    }

    /* CHECK setAdd, if true redirect to Home component */
    if (setCheckAdd) {
        return <Redirect to="/Home" />
    }

    return (
        <div className={classes.root}>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <Paper className={classes.paper}>
                        <Typography className={classes.name} variant="h4">
                            Add Product
                    </Typography>
                        <Grid item xs={12} sm container>
                            <Grid item xs={12}>
                                <Typography gutterBottom variant="subtitle1">
                                    Name
                            </Typography>
                                <TextField id="name" onChange={handleChangeTitle} name="name" label="Name" variant="outlined" className={classes.txtInput} size="small" />
                            </Grid>
                            <Grid item xs={12}>
                                <Typography gutterBottom variant="subtitle1">
                                    Description
                            </Typography>
                                <TextField id="outlined-multiline-static" onChange={handleChangeBody} label="Description" name="description" className={classes.txtInput} multiline rows={4} defaultValue="Description" variant="outlined" />
                            </Grid>
                            <Grid item xs={12}>
                                <Button type="button" onClick={addProduct} fullWidth variant="contained" color="primary" className={classes.submit} >
                                    Add product
                            </Button>
                            </Grid>
                        </Grid>
                    </Paper>
                </Grid>
            </Grid>
        </div>
    )
}
